$(document).ready(function() {

    $(function () {
        'use strict'
    
        $('[data-toggle="offcanvas"]').on('click', function () {
            $('.offcanvas-collapse').toggleClass('open')
        })
    })

    $('.testimonial-carousel').owlCarousel({
        nav: false,
        loop:true, 
        dots:true,   
        autoplay:true,
        autoplayTimeout:4000,
        autoplayHoverPause:true,
        responsiveClass:true,
        responsive:{
            0:{
                items:1, 
            },
            420:{
                items:1, 
            },
            576:{
                margin:1,
                // loop:false, 
                // center: true,
                items:1, 
            },
            768:{
                center: false,
                // margin:110,
                items:2, 
            },
            992:{
                center: true,
                items:3, 
            },
            1200:{
                center: true,
                // margin:60,
                items:3
            },
        }
    })
     
    // aos animation
    //  AOS.init(); 
    

    $(window).on("scroll", function () {
    var lastScrollTop = '';

    function stickyMenu() {
        var st = $(window).scrollTop();  
        var mainMenuBottom = $('#top-header');

        if ($(window).scrollTop() > 10) {
            if (st > lastScrollTop) {
                //to hide sticky menu on scroll down addClass replace to removeclass
                mainMenuBottom.addClass("shadow bg-primary");

            } else {
                // active sticky menu on scroll up
                mainMenuBottom.removeClass("shadow bg-primary");
            }

        } else {
            mainMenuBottom.removeClass("shadow bg-primary");
        }

        lastScrollTop = st;
    }
    stickyMenu();

    });
});
 

  